graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Annotation System Examples</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "BaseProcessor" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>BaseProcessor</b> <i>&lt;task&gt;</i> <i>@Abstract</i></td></tr><tr><td align="left">Base processor that all processors must<br/>extend</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="version__key" align="left" balign="left">version : string</td><td port="version__value" align="left" balign="left">1.0.0</td></tr></table></td></tr></table>>, shape=egg, fillcolor="#E3F2FD", style="filled,dashed", color="#1976D2"];
  "ConfigManager" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ConfigManager</b> <i>&lt;task&gt;</i> <i>@Singleton</i></td></tr><tr><td align="left">Manages application configuration</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="configPath__key" align="left" balign="left">configPath : string</td><td port="configPath__value" align="left" balign="left">/etc/app/config.json</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", peripheries=2];
  "LegacyAPI" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>LegacyAPI</b> <i>&lt;task&gt;</i> <i>@Deprecated("Use ModernAPI instead - will be removed in v2.0")</i></td></tr><tr><td align="left">Old API endpoint - do not use in new<br/>code</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="endpoint__key" align="left" balign="left">endpoint : string</td><td port="endpoint__value" align="left" balign="left">/api/v1/legacy</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style="filled,dashed", color="#1976D2", fontcolor="#999999"];
  "ModernAPI" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ModernAPI</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">New API endpoint with better performance</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="endpoint__key" align="left" balign="left">endpoint : string</td><td port="endpoint__value" align="left" balign="left">/api/v2/modern</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "PaymentProcessor" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>PaymentProcessor</b> <i>&lt;task&gt;</i> <i>@Critical @Async</i></td></tr><tr><td align="left">Processes payments - requires high<br/>availability</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="timeout__key" align="left" balign="left">timeout : number</td><td port="timeout__value" align="left" balign="left">30000</td></tr><tr><td port="retries__key" align="left" balign="left">retries : number</td><td port="retries__value" align="left" balign="left">5</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", penwidth=3];
  "DataValidator" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>DataValidator</b> <i>&lt;task&gt;</i> <i>@Abstract @Critical</i></td></tr><tr><td align="left">Validates critical data before<br/>processing</td></tr></table>>, shape=egg, fillcolor="#E3F2FD", style="filled,dashed", color="#1976D2", penwidth=3];
  "JSONValidator" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>JSONValidator</b> <i>&lt;task&gt;</i> <i>@Singleton</i></td></tr><tr><td align="left">Validates JSON data</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="schema__key" align="left" balign="left">schema : string</td><td port="schema__value" align="left" balign="left">schema.json</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", peripheries=2];
  "XMLValidator" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>XMLValidator</b> <i>&lt;task&gt;</i> <i>@Deprecated("Use JSONValidator instead")</i></td></tr><tr><td align="left">Validates XML data - legacy system</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style="filled,dashed", color="#1976D2", fontcolor="#999999"];
  "FetchData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>FetchData</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Asynchronously fetches data from<br/>external source</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="url__key" align="left" balign="left">url : string</td><td port="url__value" align="left" balign="left">https://api.example.com/data</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "ProcessData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ProcessData</b> <i>&lt;task&gt;</i> <i>@Async @Critical</i></td></tr><tr><td align="left">Processes fetched data asynchronously</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", penwidth=3];
  "StoreData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>StoreData</b> <i>&lt;task&gt;</i> <i>@Critical</i></td></tr><tr><td align="left">Stores processed data in database</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", penwidth=3];

  // Edges
  "BaseProcessor" -> "DataValidator" [arrowhead=empty, dir=back, labelOverlay="75%", labelhref="#srcLineTBD"];
  "DataValidator" -> "JSONValidator" [arrowhead=empty, dir=back, labelOverlay="75%", labelhref="#srcLineTBD"];
  "DataValidator" -> "XMLValidator" [arrowhead=empty, dir=back, labelOverlay="75%", labelhref="#srcLineTBD"];
  "FetchData" -> "ProcessData" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "ProcessData" -> "StoreData" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "ConfigManager" -> "PaymentProcessor" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ConfigManager" -> "FetchData" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
