graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Annotated System</b></font></td></tr><tr><td align="center"><font point-size="10">v @Version(&quot;2.0&quot;) @Critical @StrictMode</font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "important" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>important</b> <i>&lt;task&gt;</i> <i>@Critical @Async</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="priority__key" align="left" balign="left">priority : number</td><td port="priority__value" align="left" balign="left">10</td></tr><tr><td port="retries__key" align="left" balign="left">retries : number</td><td port="retries__value" align="left" balign="left">3</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", penwidth=3];
  "experimental" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>experimental</b> <i>&lt;task&gt;</i> <i>@Beta @Unstable</i></td></tr><tr><td align="left">Experimental feature</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "legacy" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>legacy</b> <i>&lt;resource&gt;</i> <i>@Deprecated("Use newResource instead")</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="endpoint__key" align="left" balign="left">endpoint</td><td port="endpoint__value" align="left" balign="left">old-api.com</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style="filled,dashed", color="#388E3C", fontcolor="#999999"];
  "scheduled" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>scheduled</b> <i>&lt;task&gt;</i> <i>@Cron("0 */6 * * *")</i></td></tr><tr><td align="left">Run every 6 hours</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "draft" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>draft</b> <i>&lt;state&gt;</i> <i>@Transient</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "persisted" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>persisted</b> <i>&lt;state&gt;</i> <i>@Persistent</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

}
