graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Documented System</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>process</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left"><font point-size="8" color="#666666">â†’ Process data</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L3:0-5:2"];
  "active" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>active</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Active State</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2", URL="#L14:0-14:28"];

  // Root-level notes
  "note_process_0" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><i>@Documentation</i></td></tr><tr><td align="left">This task processes incoming requests</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="complexity__key" align="left" balign="left">complexity</td><td port="complexity__value" align="left" balign="left">O(n)</td></tr><tr><td port="author__key" align="left" balign="left">author</td><td port="author__value" align="left" balign="left">Team A</td></tr><tr><td port="updated__key" align="left" balign="left">updated</td><td port="updated__value" align="left" balign="left">2024-01-15</td></tr><tr><td port="rationale__key" align="left" balign="left">rationale</td><td port="rationale__value" align="left" balign="left">Optimized for throughput</td></tr></table></td></tr></table>>, shape=note, fillcolor="#FFFACD", style=filled, fontsize=9];
  "note_process_0" -> "process" [style=dashed, color="#999999", arrowhead=none];
  "note_active_0" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left">Active processing state</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="entry_conditions__key" align="left" balign="left">entry_conditions</td><td port="entry_conditions__value" align="left" balign="left">All prerequisites met</td></tr><tr><td port="exit_conditions__key" align="left" balign="left">exit_conditions</td><td port="exit_conditions__value" align="left" balign="left">Processing complete or timeout</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout : number</td><td port="timeout__value" align="left" balign="left">300</td></tr></table></td></tr></table>>, shape=note, fillcolor="#FFFACD", style=filled, fontsize=9];
  "note_active_0" -> "active" [style=dashed, color="#999999", arrowhead=none];

}
