graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Enterprise System</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  subgraph cluster_backend {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>backend</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "backend__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    subgraph cluster_api {
      label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>api</b></td></tr></table>>;
      labelloc="t";
      style=filled;
      fontsize=10;
      fillcolor="#FFFFFF";
      color="#999999";
      "api__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

      "authenticate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>authenticate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Auth</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
      "validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Validate</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
      "process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>process</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
      "process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>process</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
    }
    subgraph cluster_database {
      label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>database</b></td></tr></table>>;
      labelloc="t";
      style=filled;
      fontsize=10;
      fillcolor="#FFFFFF";
      color="#999999";
      "database__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

      "read" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>read</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
      "write" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>write</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Write DB</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
      "read" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>read</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
    }
  }
  subgraph cluster_frontend {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>frontend</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "frontend__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "render" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>render</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Render UI</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "handleInput" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>handleInput</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Handle Input</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  }
  subgraph cluster_api {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>api</b></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "api__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "authenticate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>authenticate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Auth</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Validate</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>process</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
    "process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>process</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  }
  subgraph cluster_database {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>database</b></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "database__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "read" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>read</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
    "write" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>write</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Write DB</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "read" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>read</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  }

  // Edges
  "handleInput" -> "authenticate" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "write" -> "render" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "process" -> "read" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "authenticate" -> "validate" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "validate" -> "process" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "read" -> "write" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "render" -> "handleInput" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
