  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Advanced E-Commerce System</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "AppConfig" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>AppConfig</b> <i>&lt;context&gt;</i> <i>@Singleton</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="apiEndpoint__key" align="left" balign="left">apiEndpoint : string</td><td port="apiEndpoint__value" align="left" balign="left">https://api.example.com</td></tr><tr><td port="maxRetries__key" align="left" balign="left">maxRetries : number</td><td port="maxRetries__value" align="left" balign="left">3</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout : number</td><td port="timeout__value" align="left" balign="left">5000</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", peripheries=2];
