  "AuthHandler" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>AuthHandler</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Authenticate user</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
