  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b> <i>&lt;init&gt;</i></td></tr><tr><td align="left">User Session Start</td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "authenticated" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>authenticated</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Authenticated State</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "error" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>error</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Error State</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "BaseHandler" -> "AuthHandler" [arrowhead=empty, dir=back, labelOverlay="75%", labelhref="#srcLineTBD"];
  "BaseHandler" -> "OrderHandler" [arrowhead=empty, dir=back, labelOverlay="75%", labelhref="#srcLineTBD"];
  "start" -> "AuthHandler" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "AuthHandler" -> "AppConfig" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "AuthHandler" -> "authenticated" [label="success", labelOverlay="75%", labelhref="#srcLineTBD"];
  "AuthHandler" -> "error" [label="failure", labelOverlay="75%", labelhref="#srcLineTBD"];
  "authenticated" -> "OrderHandler" [labelOverlay="75%", labelhref="#srcLineTBD"];

  // Notes
  "note_0_AuthHandler" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left">Authenticates user credentials.
Uses JWT<br/>tokens with {{ AppConfig.timeout }}ms<br/>timeout.
Retries up to {{<br/>AppConfig.maxRetries }} times on<br/>failure.</td></tr></table>>, shape=note, fillcolor="#FFFACD", style=filled, fontsize=9];
  "note_0_AuthHandler" -> "AuthHandler" [style=dashed, color="#999999", arrowhead=none];

  // Validation Warnings
  "warning_OrderHandler_0" [label=<ðŸ”´ Type mismatch: expected<br/>Promise&lt;Order&gt;, got null>, shape=note, fillcolor="#FFCCCC", style=filled, fontsize=9];
  "warning_OrderHandler_0" -> "OrderHandler" [style=dashed, color="#999999", arrowhead=none];

  // Inferred Dependencies
  "OrderHandler" -> "AppConfig" [label="reads prompt", style=dashed, color=blue];
}
