graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  subgraph cluster_DataPipeline {
    label="";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "DataPipeline" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left" port="cluster_header"><b>DataPipeline</b> <i>&lt;task&gt;</i></td></tr></table>>, shape=plain, margin=0];
    "DataPipeline__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "pipelineState" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>pipelineState</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="recordsProcessed__key" align="left" balign="left">recordsProcessed</td><td port="recordsProcessed__value" align="left" balign="left">0</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
    "validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validate</b> <i>&lt;task&gt;</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  }

}
