graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Analysis Pipeline</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "analyze" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analyze</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Analyze input data and store results in<br/>multiple contexts</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "metrics" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>metrics</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="count__key" align="left" balign="left">count : number</td><td port="count__value" align="left" balign="left">0</td></tr><tr><td port="average__key" align="left" balign="left">average : number</td><td port="average__value" align="left" balign="left">0</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "summary" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>summary</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="text__key" align="left" balign="left">text : string</td><td port="text__value" align="left" balign="left"></td></tr><tr><td port="confidence__key" align="left" balign="left">confidence : number</td><td port="confidence__value" align="left" balign="left">0</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "report" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>report</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Create report: {{metrics.count}} items,<br/>avg {{metrics.average}}, summary:<br/>{{summary.text}}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "analyze" -> "metrics" [label="populates", labelOverlay="75%", labelhref="#srcLineTBD"];
  "analyze" -> "summary" [label="creates", labelOverlay="75%", labelhref="#srcLineTBD"];
  "metrics" -> "report" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "summary" -> "report" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "report" -> "metrics" [label="reads prompt", style=dashed, color=blue];
  "report" -> "metrics" [label="reads prompt", style=dashed, color=blue];
  "report" -> "summary" [label="reads prompt", style=dashed, color=blue];
}
