attributes.value:
Generate a report for {{userData.firstName + " " + userData.lastName}}.

Progress: {{userData.tasksCompleted}} / {{userData.totalTasks}} tasks completed.
Priority: {{config.priority}}
Timeout: {{config.timeout / 1000}} seconds

{{userData.tasksCompleted == userData.totalTasks ? "All tasks completed!" : "Tasks in progress..."}}

graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Dynamic Reporting</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "userData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>userData</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="firstName__key" align="left" balign="left">firstName : string</td><td port="firstName__value" align="left" balign="left">John</td></tr><tr><td port="lastName__key" align="left" balign="left">lastName : string</td><td port="lastName__value" align="left" balign="left">Doe</td></tr><tr><td port="tasksCompleted__key" align="left" balign="left">tasksCompleted : number</td><td port="tasksCompleted__value" align="left" balign="left">8</td></tr><tr><td port="totalTasks__key" align="left" balign="left">totalTasks : number</td><td port="totalTasks__value" align="left" balign="left">10</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>config</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="priority__key" align="left" balign="left">priority : string</td><td port="priority__value" align="left" balign="left">high</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout : number</td><td port="timeout__value" align="left" balign="left">5000</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "generateReport" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>generateReport</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Generate a report for<br/>{{userData.firstName + &quot; &quot; +<br/>userData.lastName}}.

Progress:<br/>{{userData.tasksCompleted}} /<br/>{{userData.totalTasks}} tasks<br/>completed.
Priority:<br/>{{config.priority}}
Timeout:<br/>{{config.timeout / 1000}}<br/>seconds

{{userData.tasksCompleted ==<br/>userData.totalTasks ? &quot;All tasks<br/>completed!&quot; : &quot;Tasks in progress...&quot;}}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "userData" -> "generateReport" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "config" -> "generateReport" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "generateReport" -> "userData" [label="reads prompt", style=dashed, color=blue];
  "generateReport" -> "userData" [label="reads prompt", style=dashed, color=blue];
  "generateReport" -> "config" [label="reads prompt", style=dashed, color=blue];
}
