graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Annotation Examples</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "BaseProcessor" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>BaseProcessor</b> <i>&lt;task&gt;</i> <i>@Abstract</i></td></tr><tr><td align="left">Base processor that all processors<br/>extend</td></tr></table>>, shape=egg, fillcolor="#E3F2FD", style="filled,dashed", color="#1976D2"];
  "DataProcessor" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>DataProcessor</b> <i>&lt;task&gt;</i> <i>@Singleton</i></td></tr><tr><td align="left">Processes data (only one instance<br/>allowed)</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", peripheries=2];
  "LegacyProcessor" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>LegacyProcessor</b> <i>&lt;task&gt;</i> <i>@Deprecated("Use DataProcessor instead")</i></td></tr><tr><td align="left">Old processor - do not use</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style="filled,dashed", color="#1976D2", fontcolor="#999999"];
  "CriticalTask" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>CriticalTask</b> <i>&lt;task&gt;</i> <i>@Critical @Async</i></td></tr><tr><td align="left">Critical async task</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="priority__key" align="left" balign="left">priority</td><td port="priority__value" align="left" balign="left">10</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", penwidth=3];

  // Edges
  "BaseProcessor" -> "DataProcessor" [arrowhead=empty, dir=back, labelOverlay="75%", labelhref="#srcLineTBD"];
}
