graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Dependency Inference Example</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>config</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="apiKey__key" align="left" balign="left">apiKey : string</td><td port="apiKey__value" align="left" balign="left">secret123</td></tr><tr><td port="apiUrl__key" align="left" balign="left">apiUrl : string</td><td port="apiUrl__value" align="left" balign="left">https://api.example.com</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout : number</td><td port="timeout__value" align="left" balign="left">5000</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "apiCall" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>apiCall</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Make API call to {{ config.apiUrl }}<br/>with key {{ config.apiKey }}</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="timeout__key" align="left" balign="left">timeout</td><td port="timeout__value" align="left" balign="left">10</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "processResponse" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>processResponse</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Process the response from {{<br/>apiCall.prompt }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "apiCall" -> "processResponse" [labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "apiCall" -> "config" [label="reads prompt", style=dashed, color=blue];
  "apiCall" -> "config" [label="reads prompt", style=dashed, color=blue];
  "processResponse" -> "apiCall" [label="reads prompt", style=dashed, color=blue];
}
