graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Complete Expressivity Example</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "apiConfig" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>apiConfig</b> <i>&lt;context&gt;</i> <i>@Singleton</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="baseUrl__key" align="left" balign="left">baseUrl : string</td><td port="baseUrl__value" align="left" balign="left">https://api.example.com</td></tr><tr><td port="apiKey__key" align="left" balign="left">apiKey : string</td><td port="apiKey__value" align="left" balign="left">secret</td></tr><tr><td port="retries__key" align="left" balign="left">retries : number</td><td port="retries__value" align="left" balign="left">3</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", peripheries=2];
  "BaseTask" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>BaseTask</b> <i>&lt;task&gt;</i> <i>@Abstract</i></td></tr><tr><td align="left">Base task for all operations</td></tr></table>>, shape=egg, fillcolor="#E3F2FD", style="filled,dashed", color="#1976D2"];
  "FetchData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>FetchData</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Fetch data from {{ apiConfig.baseUrl }}</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="timeout__key" align="left" balign="left">timeout</td><td port="timeout__value" align="left" balign="left">5000</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "ProcessData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ProcessData</b> <i>&lt;task&gt;</i> <i>@Critical</i></td></tr><tr><td align="left">Process {{ FetchData.prompt }} results</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="retries__key" align="left" balign="left">retries</td><td port="retries__value" align="left" balign="left">2</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", penwidth=3];
  "ValidateData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ValidateData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Validate the processed data</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "Success" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Success</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Operation completed successfully</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Error" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Error</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Operation failed</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "BaseTask" -> "FetchData" [arrowhead=empty, dir=back, labelOverlay="75%", labelhref="#srcLineTBD"];
  "BaseTask" -> "ProcessData" [arrowhead=empty, dir=back, labelOverlay="75%", labelhref="#srcLineTBD"];
  "FetchData" -> "ProcessData" [taillabel="1", headlabel="1", style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ProcessData" -> "ValidateData" [taillabel="1", headlabel="0..1", style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ValidateData" -> "Success" [taillabel="1", headlabel="1", style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ValidateData" -> "Error" [taillabel="1", headlabel="0..1", style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "FetchData" -> "apiConfig" [label="reads prompt", style=dashed, color=blue];
  "ProcessData" -> "FetchData" [label="reads prompt", style=dashed, color=blue];
}
