graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>User Management</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>config</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="maxOrders__key" align="left" balign="left">maxOrders : number</td><td port="maxOrders__value" align="left" balign="left">100</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "User" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>User</b> <i>&lt;task&gt;</i> <i>@Abstract</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="name__key" align="left" balign="left">name : string</td><td port="name__value" align="left" balign="left">TBD</td></tr></table></td></tr></table>>, shape=egg, fillcolor="#E3F2FD", style="filled,dashed", color="#1976D2"];
  "Order" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Order</b> <i>&lt;task&gt;</i> <i>@Singleton</i></td></tr><tr><td align="left">Create order for user (max: {{<br/>config.maxOrders }})</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", peripheries=2];

  // Edges
  "User" -> "Order" [taillabel="1", headlabel="*", style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "Order" -> "config" [label="reads prompt", style=dashed, color=blue];
}
