graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Template Demo</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "input" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>input</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="query__key" align="left" balign="left">query : string</td><td port="query__value" align="left" balign="left">analyze sentiment</td></tr><tr><td port="language__key" align="left" balign="left">language : string</td><td port="language__value" align="left" balign="left">english</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "analyze" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analyze</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Analyze the {{input.language}} text:<br/>{{input.query}}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "output" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>output</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="result__key" align="left" balign="left">result : string</td><td port="result__value" align="left" balign="left"></td></tr><tr><td port="confidence__key" align="left" balign="left">confidence : number</td><td port="confidence__value" align="left" balign="left">0</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];

  // Edges
  "input" -> "analyze" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "analyze" -> "output" [label="produces", labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "analyze" -> "input" [label="reads prompt", style=dashed, color=blue];
  "analyze" -> "input" [label="reads prompt", style=dashed, color=blue];
}
