graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>User Authentication System</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>config</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="maxRetries__key" align="left" balign="left">maxRetries : number</td><td port="maxRetries__value" align="left" balign="left">3</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout : number</td><td port="timeout__value" align="left" balign="left">30000</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "landing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>landing</b> <i>&lt;init&gt;</i></td></tr><tr><td align="left">Landing Page</td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "authenticate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>authenticate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Verify Credentials</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "authenticated" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>authenticated</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">User Authenticated</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "locked" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>locked</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Account Locked</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "maxRetries" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>maxRetries</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "timeout" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>timeout</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];

  // Edges
  "landing" -> "authenticate" [label="user login", labelOverlay="75%", labelhref="#srcLineTBD"];
  "authenticate" -> "authenticated" [label="success", labelOverlay="75%", labelhref="#srcLineTBD"];
  "authenticate" -> "landing" [label="failure", labelOverlay="75%", labelhref="#srcLineTBD"];
}
