graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>config</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="mode__key" align="left" balign="left">mode : string</td><td port="mode__value" align="left" balign="left">production</td></tr><tr><td port="maxRetries__key" align="left" balign="left">maxRetries : number</td><td port="maxRetries__value" align="left" balign="left">3</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "adapter" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>adapter</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">If mode=production and maxRetries&gt;1, add<br/>retry logic</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "adapter" -> "config" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
}
