graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Comprehensive Relationship Example</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "BaseProcessor" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>BaseProcessor</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Base Processor</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "DataProcessor" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>DataProcessor</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Data Processor</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "Validator" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Validator</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Data Validator</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "Config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Config</b> <i>&lt;context&gt;</i></td></tr><tr><td align="left">Configuration</td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "Storage" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Storage</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Data Storage</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "BaseProcessor" -> "DataProcessor" [arrowhead=empty, dir=back, labelOverlay="75%", labelhref="#srcLineTBD"];
  "DataProcessor" -> "Validator" [arrowhead=diamond, arrowtail=diamond, dir=forward, labelOverlay="75%", labelhref="#srcLineTBD"];
  "DataProcessor" -> "Storage" [arrowhead=odiamond, arrowtail=none, labelOverlay="75%", labelhref="#srcLineTBD"];
  "DataProcessor" -> "Config" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "Validator" -> "Storage" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "DataProcessor" -> "Storage" [dir=both, arrowhead=normal, arrowtail=normal, labelOverlay="75%", labelhref="#srcLineTBD"];
}
