graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Inheritance Validation</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "BaseProcessor" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>BaseProcessor</b> <i>&lt;task&gt;</i> <i>@Abstract</i></td></tr></table>>, shape=egg, fillcolor="#E3F2FD", style="filled,dashed", color="#1976D2"];
  "Processor" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Processor</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "BaseProcessor" -> "Processor" [arrowhead=empty, dir=back, labelOverlay="75%", labelhref="#srcLineTBD"];
}
