graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Validation Complete Example</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "apiConfig" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>apiConfig</b> <i>&lt;context&gt;</i> <i>@Singleton</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="baseUrl__key" align="left" balign="left">baseUrl : string</td><td port="baseUrl__value" align="left" balign="left">https://api.example.com</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout : number</td><td port="timeout__value" align="left" balign="left">5000</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", peripheries=2];
  "BaseTask" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>BaseTask</b> <i>&lt;task&gt;</i> <i>@Abstract</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="retries__key" align="left" balign="left">retries : number</td><td port="retries__value" align="left" balign="left">3</td></tr></table></td></tr></table>>, shape=egg, fillcolor="#E3F2FD", style="filled,dashed", color="#1976D2"];
  "FetchData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>FetchData</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="url__key" align="left" balign="left">url : string</td><td port="url__value" align="left" balign="left">{{ apiConfig.baseUrl }}/data</td></tr><tr><td port="response__key" align="left" balign="left">response : Promise&lt;Response&gt;</td><td port="response__value" align="left" balign="left">pending</td></tr></table></td></tr><tr><td align="left" bgcolor="#FFCCCC"><font point-size="8"><b>ðŸ”´ 1 issue</b><br/>Type mismatch: expected Promise&lt;Response&gt;, got str...<br/></font></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", penwidth=3, color="#D32F2F"];
  "ProcessData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ProcessData</b> <i>&lt;task&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="input__key" align="left" balign="left">input : Array&lt;Record&gt;</td><td port="input__value" align="left" balign="left">[]</td></tr><tr><td port="output__key" align="left" balign="left">output : Map&lt;string, any&gt;</td><td port="output__value" align="left" balign="left">[]</td></tr></table></td></tr><tr><td align="left" bgcolor="#FFCCCC"><font point-size="8"><b>ðŸ”´ 2 issues</b><br/>Type mismatch: expected Array&lt;Record&gt;, got Array&lt;R...<br/>Type mismatch: expected Map&lt;string, any&gt;, got Map&lt;...<br/></font></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", penwidth=3, color="#D32F2F"];
  "Success" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Success</b> <i>&lt;state&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="status__key" align="left" balign="left">status : string</td><td port="status__value" align="left" balign="left">completed</td></tr></table></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Error" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Error</b> <i>&lt;state&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="message__key" align="left" balign="left">message : string</td><td port="message__value" align="left" balign="left">failed</td></tr></table></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b> <i>&lt;init&gt;</i></td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];

  // Edges
  "BaseTask" -> "FetchData" [arrowhead=empty, dir=back, labelOverlay="75%", labelhref="#srcLineTBD"];
  "BaseTask" -> "ProcessData" [arrowhead=empty, dir=back, labelOverlay="75%", labelhref="#srcLineTBD"];
  "start" -> "FetchData" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "FetchData" -> "ProcessData" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "ProcessData" -> "Success" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "ProcessData" -> "Error" [labelOverlay="75%", labelhref="#srcLineTBD"];

  // Validation Warnings
  "warning_FetchData_0" [label=<ðŸ”´ Type mismatch: expected<br/>Promise&lt;Response&gt;, got string>, shape=note, fillcolor="#FFCCCC", style=filled, fontsize=9];
  "warning_FetchData_0" -> "FetchData" [style=dashed, color="#999999", arrowhead=none];
  "warning_ProcessData_0" [label=<ðŸ”´ Type mismatch: expected<br/>Array&lt;Record&gt;, got Array&lt;Record&gt;.<br/>Expected array, received string>, shape=note, fillcolor="#FFCCCC", style=filled, fontsize=9];
  "warning_ProcessData_0" -> "ProcessData" [style=dashed, color="#999999", arrowhead=none];
  "warning_ProcessData_1" [label=<ðŸ”´ Type mismatch: expected Map&lt;string,<br/>any&gt;, got Map&lt;string, any&gt;. Expected<br/>object, received string>, shape=note, fillcolor="#FFCCCC", style=filled, fontsize=9];
  "warning_ProcessData_1" -> "ProcessData" [style=dashed, color="#999999", arrowhead=none];

  // Inferred Dependencies
  "FetchData" -> "apiConfig" [label="reads url", style=dashed, color=blue];
}
