graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Machine Attributes</b></font></td></tr><tr><td align="center"><font point-size="10">v @Version(&quot;2.0&quot;)</font></td></tr><tr><td align="center"><font point-size="10"><i>Production machine</i></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="author__key" align="left" balign="left">author</td><td port="author__value" align="left" balign="left">Team A</td></tr><tr><td port="created__key" align="left" balign="left">created</td><td port="created__value" align="left" balign="left">2024-01-15</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout : number</td><td port="timeout__value" align="left" balign="left">60000</td></tr><tr><td port="environment__key" align="left" balign="left">environment</td><td port="environment__value" align="left" balign="left">production</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>process</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Process in {{ machine.environment }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

}
