digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Deep Attributes Test</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "node1" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>node1</b></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="name__key" align="left" balign="left">name : string</td><td port="name__value" align="left" balign="left">Primary Node</td></tr><tr><td port="count__key" align="left" balign="left">count : number</td><td port="count__value" align="left" balign="left">42</td></tr><tr><td port="enabled__key" align="left" balign="left">enabled : boolean</td><td port="enabled__value" align="left" balign="left">true</td></tr><tr><td port="items__key" align="left" balign="left">items</td><td port="items__value" align="left" balign="left">[<br align="left"/>  &quot;alpha&quot;,<br align="left"/>  &quot;beta&quot;,<br align="left"/>  &quot;gamma&quot;<br align="left"/>]</td></tr><tr><td port="ratio__key" align="left" balign="left">ratio : number</td><td port="ratio__value" align="left" balign="left">3.14159</td></tr><tr><td port="status__key" align="left" balign="left">status</td><td port="status__value" align="left" balign="left">active</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "node2" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>node2</b></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="config__key" align="left" balign="left">config</td><td port="config__value" align="left" balign="left">[<br align="left"/>  &quot;opt1&quot;,<br align="left"/>  &quot;opt2&quot;,<br align="left"/>  &quot;opt3&quot;<br align="left"/>]</td></tr><tr><td port="maxValue__key" align="left" balign="left">maxValue : number</td><td port="maxValue__value" align="left" balign="left">99999</td></tr><tr><td port="minValue__key" align="left" balign="left">minValue : number</td><td port="minValue__value" align="left" balign="left">0</td></tr><tr><td port="description__key" align="left" balign="left">description : string</td><td port="description__value" align="left" balign="left">This is a very long<br align="left"/>description that contains<br align="left"/>multiple words and should be<br align="left"/>preserved exactly as written<br align="left"/>in the transformation pipeline</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];

  // Edges
  "node1" -> "node2" [label="config=primary", labelOverlay="75%", labelhref="#srcLineTBD"];
}
