graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Context Heavy Machine</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "appConfig" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>appConfig</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="environment__key" align="left" balign="left">environment : string</td><td port="environment__value" align="left" balign="left">production</td></tr><tr><td port="version__key" align="left" balign="left">version : string</td><td port="version__value" align="left" balign="left">2.0.1</td></tr><tr><td port="debug__key" align="left" balign="left">debug : boolean</td><td port="debug__value" align="left" balign="left">false</td></tr><tr><td port="maxConnections__key" align="left" balign="left">maxConnections : number</td><td port="maxConnections__value" align="left" balign="left">1000</td></tr><tr><td port="features__key" align="left" balign="left">features</td><td port="features__value" align="left" balign="left">[<br align="left"/>  &quot;auth&quot;,<br align="left"/>  &quot;logging&quot;,<br align="left"/>  &quot;metrics&quot;<br align="left"/>]</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "userPrefs" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>userPrefs</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="theme__key" align="left" balign="left">theme : string</td><td port="theme__value" align="left" balign="left">dark</td></tr><tr><td port="language__key" align="left" balign="left">language : string</td><td port="language__value" align="left" balign="left">en-US</td></tr><tr><td port="notifications__key" align="left" balign="left">notifications : boolean</td><td port="notifications__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "bootstrap" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>bootstrap</b> <i>&lt;init&gt;</i></td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "ready" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ready</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "bootstrap" -> "ready" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
