graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Template Variable Demo</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "generateData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>generateData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">generate a random number between 1-100<br/>and store it in the data context using<br/>set_context_value</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "data" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>data</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="number__key" align="left" balign="left">number : number</td><td port="number__value" align="left" balign="left">0</td></tr><tr><td port="processed__key" align="left" balign="left">processed : boolean</td><td port="processed__value" align="left" balign="left">false</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "processData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>processData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">The number is {{data.number}}. Calculate<br/>its square and store the result in<br/>results context</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "results2" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>results2</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="original__key" align="left" balign="left">original : number</td><td port="original__value" align="left" balign="left">0</td></tr><tr><td port="squared__key" align="left" balign="left">squared : number</td><td port="squared__value" align="left" balign="left">0</td></tr><tr><td port="message__key" align="left" balign="left">message : string</td><td port="message__value" align="left" balign="left"></td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "end" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>end</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "start" -> "generateData" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "generateData" -> "data" [label="stores", labelOverlay="75%", labelhref="#srcLineTBD"];
  "data" -> "processData" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "processData" -> "results2" [label="calculates", labelOverlay="75%", labelhref="#srcLineTBD"];
  "results2" -> "end" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "processData" -> "data" [label="reads prompt", style=dashed, color=blue];
}
