  "start" -> "LoginTask" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "LoginTask" -> "ValidateSession" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "ValidateSession" -> "MainMenu" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "MainMenu" -> "ProcessPayment" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "ProcessPayment" -> "ConfirmOrder" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "ConfirmOrder" -> "end" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "MainMenu" -> "ViewOrders" [labelOverlay="75%", labelhref="#srcLineTBD"];
