graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Payment Processing</b></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="prompt__key" align="left" balign="left">prompt</td><td port="prompt__value" align="left" balign="left">Process payment with payment<br align="left"/>gateway</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "ProcessPayment" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ProcessPayment</b> <i>&lt;task&gt;</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "RefundPayment" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>RefundPayment</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Issue refund to customer</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "PaymentResult" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>PaymentResult</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="success__key" align="left" balign="left">success</td><td port="success__value" align="left" balign="left">boolean</td></tr><tr><td port="transactionId__key" align="left" balign="left">transactionId</td><td port="transactionId__value" align="left" balign="left">string</td></tr><tr><td port="amount__key" align="left" balign="left">amount</td><td port="amount__value" align="left" balign="left">number</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  subgraph cluster_llm {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>llm</b></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "llm__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "temperature" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>temperature</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  }

  // Edges
  "ProcessPayment" -> "PaymentResult" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
