graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>CI/CD Pipeline</b></font></td></tr><tr><td align="center"><font point-size="10">v @Version(&quot;1.0&quot;)</font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "trigger" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>trigger</b> <i>&lt;input&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="event__key" align="left" balign="left">event</td><td port="event__value" align="left" balign="left">push</td></tr><tr><td port="branch__key" align="left" balign="left">branch</td><td port="branch__value" align="left" balign="left">main</td></tr><tr><td port="commit__key" align="left" balign="left">commit</td><td port="commit__value" align="left" balign="left"></td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  subgraph cluster_build {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>build</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "build__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "checkout" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>checkout</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Checkout code</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "installDeps" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>installDeps</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Install dependencies</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "compile" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>compile</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Compile</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "lint" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>lint</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Run linter</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  }
  subgraph cluster_test {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>test</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "test__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "unitTest" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>unitTest</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Run unit tests</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "integrationTest" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>integrationTest</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Run integration tests</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "securityScan" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>securityScan</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Security scan</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  }
  "testsPass" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>testsPass</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Tests Pass</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "testsFail" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>testsFail</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Tests Fail</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  subgraph cluster_deploy {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>deploy</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "deploy__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "buildImage" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>buildImage</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Build Docker image</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "pushImage" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>pushImage</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Push to registry</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "deployStaging" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>deployStaging</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Deploy to staging</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "runSmokeTests" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>runSmokeTests</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Run smoke tests</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "stagingOk" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>stagingOk</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Staging OK</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
    "stagingFail" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>stagingFail</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Staging Fail</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
    "deployProduction" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>deployProduction</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Deploy to production</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "healthCheck" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>healthCheck</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Health check</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  }
  "result" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>result</b> <i>&lt;output&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="success__key" align="left" balign="left">success : boolean</td><td port="success__value" align="left" balign="left">false</td></tr><tr><td port="version__key" align="left" balign="left">version</td><td port="version__value" align="left" balign="left"></td></tr><tr><td port="deployedAt__key" align="left" balign="left">deployedAt</td><td port="deployedAt__value" align="left" balign="left"></td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];

  // Edges
  "trigger" -> "checkout" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "lint" -> "unitTest" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "lint" -> "integrationTest" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "lint" -> "securityScan" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "unitTest" -> "testsPass" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "integrationTest" -> "testsPass" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "securityScan" -> "testsPass" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "testsPass" -> "buildImage" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "testsFail" -> "result" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "stagingFail" -> "result" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "healthCheck" -> "result" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "checkout" -> "installDeps" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "installDeps" -> "compile" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "compile" -> "lint" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "buildImage" -> "pushImage" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "pushImage" -> "deployStaging" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "deployStaging" -> "runSmokeTests" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "runSmokeTests" -> "stagingOk" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "runSmokeTests" -> "stagingFail" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "stagingOk" -> "deployProduction" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "deployProduction" -> "healthCheck" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
