graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>ETL Pipeline</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>config</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="batchSize__key" align="left" balign="left">batchSize : number</td><td port="batchSize__value" align="left" balign="left">1000</td></tr><tr><td port="parallelism__key" align="left" balign="left">parallelism : number</td><td port="parallelism__value" align="left" balign="left">4</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  subgraph cluster_extraction {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>extraction</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "extraction__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "extractDB" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>extractDB</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Extract from Database</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="source__key" align="left" balign="left">source</td><td port="source__value" align="left" balign="left">postgresql</td></tr><tr><td port="query__key" align="left" balign="left">query</td><td port="query__value" align="left" balign="left">#extractQuery</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "extractAPI" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>extractAPI</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Extract from API</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="source__key" align="left" balign="left">source</td><td port="source__value" align="left" balign="left">rest-api</td></tr><tr><td port="endpoint__key" align="left" balign="left">endpoint</td><td port="endpoint__value" align="left" balign="left">https://api.example.com/data</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "extractFiles" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>extractFiles</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Extract from Files</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="source__key" align="left" balign="left">source</td><td port="source__value" align="left" balign="left">s3</td></tr><tr><td port="bucket__key" align="left" balign="left">bucket</td><td port="bucket__value" align="left" balign="left">data-lake</td></tr><tr><td port="pattern__key" align="left" balign="left">pattern</td><td port="pattern__value" align="left" balign="left">*.json</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "mergeExtracts" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>mergeExtracts</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Merge all sources</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  }
  subgraph cluster_transformation {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>transformation</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "transformation__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "cleanData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>cleanData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Clean data</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="operations__key" align="left" balign="left">operations</td><td port="operations__value" align="left" balign="left">[<br align="left"/>  &quot;remove_nulls&quot;,<br align="left"/>  &quot;trim_strings&quot;,<br align="left"/>  &quot;deduplicate&quot;<br align="left"/>]</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "normalizeData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>normalizeData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Normalize</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="format__key" align="left" balign="left">format</td><td port="format__value" align="left" balign="left">standard</td></tr><tr><td port="schema__key" align="left" balign="left">schema</td><td port="schema__value" align="left" balign="left">#targetSchema</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "enrichData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>enrichData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Enrich with external data</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="lookupService__key" align="left" balign="left">lookupService</td><td port="lookupService__value" align="left" balign="left">#enrichmentAPI</td></tr><tr><td port="fields__key" align="left" balign="left">fields</td><td port="fields__value" align="left" balign="left">[<br/>  &quot;location&quot;,<br/>  &quot;category&quot;<br/>]</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "validateData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validateData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Validate</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="schema__key" align="left" balign="left">schema</td><td port="schema__value" align="left" balign="left">#targetSchema</td></tr><tr><td port="strict__key" align="left" balign="left">strict : boolean</td><td port="strict__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  }
  subgraph cluster_loading {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>loading</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "loading__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "loadWarehouse" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>loadWarehouse</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Load to data warehouse</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="destination__key" align="left" balign="left">destination</td><td port="destination__value" align="left" balign="left">snowflake</td></tr><tr><td port="table__key" align="left" balign="left">table</td><td port="table__value" align="left" balign="left">processed_data</td></tr><tr><td port="mode__key" align="left" balign="left">mode</td><td port="mode__value" align="left" balign="left">append</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "updateCache" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>updateCache</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Update cache</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="cache__key" align="left" balign="left">cache</td><td port="cache__value" align="left" balign="left">redis</td></tr><tr><td port="ttl__key" align="left" balign="left">ttl : number</td><td port="ttl__value" align="left" balign="left">3600</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "notifyDownstream" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>notifyDownstream</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Notify downstream systems</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="targets__key" align="left" balign="left">targets</td><td port="targets__value" align="left" balign="left">[<br align="left"/>  &quot;analytics&quot;,<br align="left"/>  &quot;reporting&quot;<br align="left"/>]</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  }

  // Edges
  "mergeExtracts" -> "cleanData" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "validateData" -> "loadWarehouse" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "validateData" -> "updateCache" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "validateData" -> "notifyDownstream" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "extractDB" -> "mergeExtracts" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "extractAPI" -> "mergeExtracts" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "extractFiles" -> "mergeExtracts" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "cleanData" -> "normalizeData" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "normalizeData" -> "enrichData" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "enrichData" -> "validateData" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
