    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>unitTests</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "unitTests__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "runModuleA" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>runModuleA</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Unit tests: Module A</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
