graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "database" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>database</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="connection__key" align="left" balign="left">connection : string</td><td port="connection__value" align="left" balign="left">postgres://localhost/db</td></tr><tr><td port="schema__key" align="left" balign="left">schema : object</td><td port="schema__value" align="left" balign="left">{}</td></tr></table></td></tr><tr><td align="left" bgcolor="#FFCCCC"><font point-size="8"><b>ðŸ”´ 1 issue</b><br/>Type mismatch: expected object, got string<br/></font></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", penwidth=3, color="#D32F2F"];
  "query" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>query</b></td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "update" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>update</b></td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "store" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>store</b></td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "task" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>task</b></td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "context" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>context</b></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];

  // Edges
  "query" -> "database" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
  "update" -> "database" [label="writes", labelOverlay="75%", labelhref="#srcLineTBD"];
  "store" -> "database" [label="stores", labelOverlay="75%", labelhref="#srcLineTBD"];
  "task" -> "context" [label="write=field1", labelOverlay="75%", labelhref="#srcLineTBD"];
  "task" -> "context" [label="read=field3", labelOverlay="75%", labelhref="#srcLineTBD"];

  // Validation Warnings
  "warning_database_0" [label=<ðŸ”´ Type mismatch: expected object, got<br/>string>, shape=note, fillcolor="#FFCCCC", style=filled, fontsize=9];
  "warning_database_0" -> "database" [style=dashed, color="#999999", arrowhead=none];
}
