graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Quoted Labels Machine</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "middle" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>middle</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "end" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>end</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "error" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>error</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];

  // Edges
  "start" -> "middle" [label="user clicks button", labelOverlay="75%", labelhref="#srcLineTBD"];
  "middle" -> "end" [label="validation: passed; retry: 3;", labelOverlay="75%", labelhref="#srcLineTBD"];
  "middle" -> "error" [label="error: timeout", labelOverlay="75%", labelhref="#srcLineTBD"];
  "error" -> "start" [label="retry attempt", labelOverlay="75%", labelhref="#srcLineTBD"];
}
