graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Project Management</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  subgraph cluster_development {
    label="";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "development" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left" port="cluster_header"><b>development</b> <i>&lt;process&gt;</i></td></tr></table>>, shape=plain, margin=0];
    "development__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "design" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>design</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Design system</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "implement" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>implement</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Implement features</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "test" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>test</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Run tests</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  }
  subgraph cluster_deployment {
    label="";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "deployment" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left" port="cluster_header"><b>deployment</b> <i>&lt;process&gt;</i></td></tr></table>>, shape=plain, margin=0];
    "deployment__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "build" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>build</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Build artifacts</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "stage" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>stage</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Deploy to staging</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "prod" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>prod</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Deploy to production</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  }

  // Edges
  "development__cluster_anchor" -> "deployment__cluster_anchor" [labelOverlay="75%", labelhref="#srcLineTBD", ltail="cluster_development", lhead="cluster_deployment"];
  "design" -> "implement" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "implement" -> "test" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "build" -> "stage" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "stage" -> "prod" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
