graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Relationship Types</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "input" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>input</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Input</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "output" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>output</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Output</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "parent" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>parent</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Parent Task</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "child" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>child</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Child Task</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "container" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>container</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Container</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "component" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>component</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Component</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "team" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>team</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Team</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "member" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>member</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Member</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "clientA" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>clientA</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Client A</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "server" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>server</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Server</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "data" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>data</b> <i>&lt;input&gt;</i></td></tr><tr><td align="left">Data</td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "transform" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>transform</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Transform</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "result" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>result</b> <i>&lt;output&gt;</i></td></tr><tr><td align="left">Result</td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "serviceA" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>serviceA</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Service A</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "serviceB" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>serviceB</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Service B</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "input" -> "output" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "child" -> "parent" [arrowhead=empty, dir=back, labelOverlay="75%", labelhref="#srcLineTBD"];
  "container" -> "component" [arrowhead=diamond, arrowtail=diamond, dir=forward, labelOverlay="75%", labelhref="#srcLineTBD"];
  "team" -> "member" [arrowhead=odiamond, arrowtail=none, labelOverlay="75%", labelhref="#srcLineTBD"];
  "clientA" -> "server" [dir=both, arrowhead=normal, arrowtail=normal, labelOverlay="75%", labelhref="#srcLineTBD"];
  "data" -> "transform" [penwidth=3, color="#D32F2F", labelOverlay="75%", labelhref="#srcLineTBD"];
  "transform" -> "result" [penwidth=3, color="#D32F2F", labelOverlay="75%", labelhref="#srcLineTBD"];
  "serviceA" -> "serviceB" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
