graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Labeled Edges</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Start</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>process</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Process</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "end" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>end</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">End</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "idle" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>idle</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Idle</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "active" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>active</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Active</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "sender" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>sender</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Sender</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "receiver" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>receiver</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Receiver</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "taskA" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>taskA</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Task A</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "taskB" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>taskB</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Task B</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "start" -> "process" [label="validate", labelOverlay="75%", labelhref="#srcLineTBD"];
  "process" -> "end" [label="complete", labelOverlay="75%", labelhref="#srcLineTBD"];
  "idle" -> "active" [label="s", labelOverlay="75%", labelhref="#srcLineTBD"];
  "sender" -> "receiver" [label="@style(\"color: red; stroke-width: 3px\")", labelOverlay="75%", labelhref="#srcLineTBD"];
  "taskA" -> "taskB" [label="priority=5, weight=10", labelOverlay="75%", labelhref="#srcLineTBD"];
}
