graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Nested Attributes</b></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="environment__key" align="left" balign="left">environment</td><td port="environment__value" align="left" balign="left">production</td></tr><tr><td port="region__key" align="left" balign="left">region</td><td port="region__value" align="left" balign="left">us-west-2</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  subgraph cluster_apiGateway {
    label="";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "apiGateway" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left" port="cluster_header"><b>apiGateway</b> <i>&lt;process&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="rateLimit__key" align="left" balign="left">rateLimit : number</td><td port="rateLimit__value" align="left" balign="left">1000</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout : number</td><td port="timeout__value" align="left" balign="left">30</td></tr></table></td></tr></table>>, shape=plain, margin=0];
    "apiGateway__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    subgraph cluster_authentication {
      label="";
      style=filled;
      fontsize=10;
      fillcolor="#FFFFFF";
      color="#999999";
      "authentication" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left" port="cluster_header"><b>authentication</b> <i>&lt;process&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="provider__key" align="left" balign="left">provider</td><td port="provider__value" align="left" balign="left">oauth2</td></tr><tr><td port="tokenExpiry__key" align="left" balign="left">tokenExpiry : number</td><td port="tokenExpiry__value" align="left" balign="left">3600</td></tr></table></td></tr></table>>, shape=plain, margin=0];
      "authentication__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

      "validateToken" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validateToken</b> <i>&lt;task&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="algorithm__key" align="left" balign="left">algorithm</td><td port="algorithm__value" align="left" balign="left">RS256</td></tr><tr><td port="publicKey__key" align="left" balign="left">publicKey</td><td port="publicKey__value" align="left" balign="left">#jwtPublicKey</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
      "refreshToken" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>refreshToken</b> <i>&lt;task&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="maxAge__key" align="left" balign="left">maxAge : number</td><td port="maxAge__value" align="left" balign="left">7200</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    }
    subgraph cluster_routing {
      label="";
      style=filled;
      fontsize=10;
      fillcolor="#FFFFFF";
      color="#999999";
      "routing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left" port="cluster_header"><b>routing</b> <i>&lt;process&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="strategy__key" align="left" balign="left">strategy</td><td port="strategy__value" align="left" balign="left">round-robin</td></tr><tr><td port="healthCheck__key" align="left" balign="left">healthCheck : bool</td><td port="healthCheck__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=plain, margin=0];
      "routing__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

      "routeRequest" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>routeRequest</b> <i>&lt;task&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="backends__key" align="left" balign="left">backends</td><td port="backends__value" align="left" balign="left">[<br align="left"/>  &quot;backend1&quot;,<br align="left"/>  &quot;backend2&quot;,<br align="left"/>  &quot;backend3&quot;<br align="left"/>]</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    }
  }


  // Validation Warnings
  "warning_routing_0" [label=<ðŸ”´ Type mismatch: expected bool, got<br/>boolean>, shape=note, fillcolor="#FFCCCC", style=filled, fontsize=9];
  "warning_routing_0" -> "routing" [style=dashed, color="#999999", arrowhead=none];
}
