graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Configured Tasks</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "analyze" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analyze</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Analyze the input data</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="model__key" align="left" balign="left">model</td><td port="model__value" align="left" balign="left">claude-3-5-sonnet-20241022</td></tr><tr><td port="temperature__key" align="left" balign="left">temperature</td><td port="temperature__value" align="left" balign="left">0.7</td></tr><tr><td port="max_tokens__key" align="left" balign="left">max_tokens</td><td port="max_tokens__value" align="left" balign="left">2048</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "data" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>data</b> <i>&lt;input&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="format__key" align="left" balign="left">format</td><td port="format__value" align="left" balign="left">json</td></tr><tr><td port="schema__key" align="left" balign="left">schema</td><td port="schema__value" align="left" balign="left">#inputSchema</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];

  // Edges
  "data" -> "analyze" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
