graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Document Processing</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "llm" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>llm</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="model__key" align="left" balign="left">model</td><td port="model__value" align="left" balign="left">claude-3-5-sonnet-20241022</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "document" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>document</b> <i>&lt;input&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="content__key" align="left" balign="left">content</td><td port="content__value" align="left" balign="left">#documentContent</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "extract" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>extract</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Extract key entities from: {{<br/>document.content }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "classify" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>classify</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Classify the document based on entities:<br/>{{ extract.result }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "summarize" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>summarize</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Summarize the document: {{<br/>document.content }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "combine" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>combine</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Combine classification and summary: {{<br/>classify.result }} + {{ summarize.result<br/>}}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "document" -> "extract" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "extract" -> "classify" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "classify" -> "combine" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "document" -> "summarize" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "summarize" -> "combine" [labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "extract" -> "document" [label="reads prompt", style=dashed, color=blue];
  "classify" -> "extract" [label="reads prompt", style=dashed, color=blue];
  "summarize" -> "document" [label="reads prompt", style=dashed, color=blue];
  "combine" -> "classify" [label="reads prompt", style=dashed, color=blue];
  "combine" -> "summarize" [label="reads prompt", style=dashed, color=blue];
}
