graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Order Processing</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "pending" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>pending</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order Pending</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "processing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>processing</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Processing Order</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "shipped" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>shipped</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order Shipped</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "delivered" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>delivered</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order Delivered</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Validate order</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "prepare" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>prepare</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Prepare shipment</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "ship" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ship</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Ship order</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "pending" -> "validate" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "validate" -> "processing" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "processing" -> "prepare" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "prepare" -> "shipped" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "shipped" -> "ship" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "ship" -> "delivered" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
