graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Smart Thermostat</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "idle" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>idle</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Idle</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "heating" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>heating</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Heating</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "cooling" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>cooling</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Cooling</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "checkTemp" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>checkTemp</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Check temperature</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "heatOn" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>heatOn</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Turn heater on</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "coolOn" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>coolOn</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Turn AC on</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "turnOff" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>turnOff</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Turn off</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "idle" -> "checkTemp" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "checkTemp" -> "heatOn" [label="temp < 68", labelOverlay="75%", labelhref="#srcLineTBD"];
  "heatOn" -> "heating" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "checkTemp" -> "coolOn" [label="temp > 72", labelOverlay="75%", labelhref="#srcLineTBD"];
  "coolOn" -> "cooling" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "heating" -> "checkTemp" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "cooling" -> "checkTemp" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "checkTemp" -> "turnOff" [label="68 <= temp <= 72", labelOverlay="75%", labelhref="#srcLineTBD"];
  "turnOff" -> "idle" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
