graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Connection Manager</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "disconnected" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>disconnected</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Disconnected</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  subgraph cluster_connected {
    label="";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "connected" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left" port="cluster_header"><b>connected</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left"><b>Connected</b><i></i></td></tr></table>>, shape=plain, margin=0];
    "connected__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "authenticating" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>authenticating</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Authenticating</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
    "authenticated" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>authenticated</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Authenticated</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
    "active" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>active</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Active</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  }
  "error" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>error</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Error State</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "connect" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>connect</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Establish connection</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "authenticate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>authenticate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Authenticate user</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "disconnect" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>disconnect</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Disconnect</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "handleError" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>handleError</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Handle error</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "disconnected" -> "connect" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "connect" -> "authenticating" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "active" -> "disconnect" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "disconnect" -> "disconnected" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "connected__cluster_anchor" -> "handleError" [labelOverlay="75%", labelhref="#srcLineTBD", ltail="cluster_connected"];
  "handleError" -> "error" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "error" -> "connect" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "connect" -> "authenticating" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "authenticating" -> "authenticated" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "authenticated" -> "active" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
