graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Game Session</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "session" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>session</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="score__key" align="left" balign="left">score : number</td><td port="score__value" align="left" balign="left">0</td></tr><tr><td port="lives__key" align="left" balign="left">lives : number</td><td port="lives__value" align="left" balign="left">3</td></tr><tr><td port="level__key" align="left" balign="left">level : number</td><td port="level__value" align="left" balign="left">1</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "playing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>playing</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Playing</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "paused" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>paused</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Paused</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "gameOver" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>gameOver</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Game Over</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "victory" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>victory</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Victory</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "updateScore" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>updateScore</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Update score</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "checkWinCondition" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>checkWinCondition</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Check win</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "checkLoseCondition" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>checkLoseCondition</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Check lose</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "playing" -> "updateScore" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "updateScore" -> "checkWinCondition" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "checkWinCondition" -> "victory" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">score &gt;= 1000</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "checkWinCondition" -> "checkLoseCondition" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">score &lt; 1000</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "checkLoseCondition" -> "gameOver" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">lives &lt;= 0</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "checkLoseCondition" -> "playing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">lives &gt; 0</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "updateScore" -> "session" [label="reads prompt", style=dashed, color=blue];
}
