graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Runtime Demo</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "state" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>state</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="counter__key" align="left" balign="left">counter : number</td><td port="counter__value" align="left" balign="left">0</td></tr><tr><td port="status__key" align="left" balign="left">status</td><td port="status__value" align="left" balign="left">running</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "increment" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>increment</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Increment counter: {{ state.counter }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "check" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>check</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Check if counter &gt;= 5</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "done" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>done</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Done</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "increment" -> "check" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "check" -> "increment" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">counter &lt; 5</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "check" -> "done" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">counter &gt;= 5</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "increment" -> "state" [label="reads prompt", style=dashed, color=blue];
}
