graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Styled Edges</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Start</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "critical" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>critical</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Critical Path</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "optional" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>optional</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Optional</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "end" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>end</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">End</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "start" -> "critical" [label="@style(\"color: red; stroke-width: 4px\")", labelOverlay="75%", labelhref="#srcLineTBD"];
  "start" -> "optional" [label="@style(\"color: gray; stroke-dasharray:\\n5,5\")", labelOverlay="75%", labelhref="#srcLineTBD"];
  "critical" -> "end" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "optional" -> "end" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
