graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Mixed Directions</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  subgraph cluster_vertical {
    label="";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "vertical" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left" port="cluster_header"><b>vertical</b> <i>&lt;process&gt;</i> <i>@Direction("TB")</i></td></tr></table>>, shape=plain, margin=0];
    "vertical__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "a" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>a</b> <i>&lt;task&gt;</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "c" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>c</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  }
  subgraph cluster_horizontal {
    label="";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "horizontal" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left" port="cluster_header"><b>horizontal</b> <i>&lt;process&gt;</i> <i>@Direction("LR")</i></td></tr></table>>, shape=plain, margin=0];
    "horizontal__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "x" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>x</b> <i>&lt;task&gt;</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "z" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>z</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  }
  "Task" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Task</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "b" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>b</b></td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "y" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>y</b></td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];

  // Edges
  "b" -> "Task" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "y" -> "Task" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
