graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "important" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>important</b> <i>&lt;task&gt;</i> <i>@Style("fillcolor: yellow; shape: box")</i></td></tr><tr><td align="left">Important task</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

}
