graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Horizontal Flow</b></font></td></tr><tr><td align="center"><font point-size="10">v @style</font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  rankdir="LR";
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "a" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>a</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">A</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "b" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>b</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">B</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "c" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>c</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">C</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "a" -> "b" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "b" -> "c" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
