graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Ranked Layout</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b> <i>&lt;task&gt;</i> <i>@rank("header")</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "plan" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>plan</b> <i>&lt;task&gt;</i> <i>@rank("same:planning")</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "review" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>review</b> <i>&lt;task&gt;</i> <i>@rank("same:planning")</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "archive" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>archive</b> <i>&lt;task&gt;</i> <i>@rank("footer")</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Rank directives
  // Rank: top/header nodes
  { rank=min; "start"; }
  // Rank: bottom/footer nodes
  { rank=max; "archive"; }
  // Rank group planning
  { rank=same; "plan"; "review"; }

  // Edges
  "start" -> "plan" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "plan" -> "review" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "review" -> "archive" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
