graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Conditional Workflow</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "evaluate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>evaluate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Evaluate condition</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "pathA" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>pathA</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Process path A</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "pathB" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>pathB</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Process path B</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "merge" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>merge</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Merge results</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "evaluate" -> "pathA" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "evaluate" -> "pathB" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "pathA" -> "merge" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "pathB" -> "merge" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
