graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Multi-Stage Pipeline</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "fetchAPI" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>fetchAPI</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Fetch from API</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "fetchDB" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>fetchDB</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Fetch from DB</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "merge" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>merge</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Merge sources</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "clean" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>clean</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Clean data</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "enrich" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>enrich</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Enrich data</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "toWarehouse" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>toWarehouse</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">To data warehouse</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "toCache" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>toCache</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">To cache</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "toAPI" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>toAPI</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">To API</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "fetchAPI" -> "merge" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "fetchDB" -> "merge" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "merge" -> "clean" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "clean" -> "enrich" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "enrich" -> "toWarehouse" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "enrich" -> "toCache" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "enrich" -> "toAPI" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
