graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Conditional Processing</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "request" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>request</b> <i>&lt;input&gt;</i></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "evaluate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>evaluate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Evaluate request</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "approved" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>approved</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Approved</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "rejected" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>rejected</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Rejected</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "processApproved" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>processApproved</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Process approved request</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "handleRejection" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>handleRejection</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Handle rejection</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "result" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>result</b> <i>&lt;output&gt;</i></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];

  // Edges
  "request" -> "evaluate" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "evaluate" -> "approved" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "evaluate" -> "rejected" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "approved" -> "processApproved" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "processApproved" -> "result" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "rejected" -> "handleRejection" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "handleRejection" -> "result" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
